package com.example.root.moviecatalogue.baseMVP;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.annotation.StringRes;import android.support.design.widget.Snackbar;import android.support.v4.content.ContextCompat;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.TextView;import android.widget.Toast;import com.example.root.moviecatalogue.R;import butterknife.Unbinder;public abstract class BaseActivity extends AppCompatActivity implements BaseView{    public static final String TAG_NAME = BaseActivity.class.getSimpleName();    private Unbinder unbinder;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);    }    public void setUnbinder(Unbinder unbinder) {        this.unbinder = unbinder;    }    @Override    protected void onDestroy() {        if (unbinder != null){            unbinder.unbind();        }        super.onDestroy();    }    @Override    public void onError(String message) {        if (message != null){            showSnackBar(message);        } else {            showSnackBar("Something Error !!");        }    }    @Override    public void showMessage(String message) {        if (message != null) {            Toast.makeText(this, message, Toast.LENGTH_SHORT).show();        } else {            Toast.makeText(this, "Something Error !!", Toast.LENGTH_SHORT).show();        }    }    @Override    public void showMessage(@StringRes int resId) {        showMessage(getString(resId));    }    @Override    public void onError(int id) {        onError(getString(id));    }    private void showSnackBar(String message){        Snackbar snackbar = Snackbar.make(findViewById(android.R.id.content),                message, Snackbar.LENGTH_LONG);        View sbView = snackbar.getView();        TextView textView = (TextView) sbView                .findViewById(android.support.design.R.id.snackbar_text);        textView.setTextColor(ContextCompat.getColor(this, android.R.color.white));        snackbar.show();    }    protected abstract void setUp();}