package com.example.root.moviecatalogue.baseMVP;import io.reactivex.disposables.CompositeDisposable;public class PresenterClass<V extends BaseView, I extends BaseInteractor> implements BasePresenter<V, I> {    private V view;    private I interactor;    private CompositeDisposable compositeDisposable;    public PresenterClass(I interactor, CompositeDisposable compositeDisposable) {        this.interactor = interactor;        this.compositeDisposable = compositeDisposable;    }    @Override    public void onAttach(V baseView) {        this.view = baseView;    }    @Override    public void onDetach() {        compositeDisposable.dispose();        view = null;        interactor = null;    }    @Override    public V getBaseView() {        return view;    }    @Override    public I getBaseInteractor() {        return interactor;    }    public CompositeDisposable getCompositeDisposable() {        return compositeDisposable;    }}