package com.example.root.moviecatalogue.model;import android.os.Parcel;import android.os.Parcelable;import com.google.gson.annotations.Expose;import com.google.gson.annotations.SerializedName;public class MovieModel implements Parcelable{    @SerializedName("id")    private int id;    @SerializedName("title")    private String movie_title;    @SerializedName("overview")    private String movie_overview;    @SerializedName("backdrop_path")    private String movie_backdrop;    @SerializedName("poster_path")    private String movie_poster;    @SerializedName("release_date")    private String movie_release;    public MovieModel(){    }    public MovieModel(int id, String movie_title, String movie_overview, String movie_backdrop, String movie_poster, String movie_release) {        this.id = id;        this.movie_title = movie_title;        this.movie_overview = movie_overview;        this.movie_backdrop = movie_backdrop;        this.movie_poster = movie_poster;        this.movie_release = movie_release;    }    protected MovieModel(Parcel in) {        id = in.readInt();        movie_title = in.readString();        movie_overview = in.readString();        movie_backdrop = in.readString();        movie_poster = in.readString();        movie_release = in.readString();    }    public static final Parcelable.Creator<MovieModel> CREATOR = new Parcelable.Creator<MovieModel>() {        @Override        public MovieModel createFromParcel(Parcel in) {            return new MovieModel(in);        }        @Override        public MovieModel[] newArray(int size) {            return new MovieModel[size];        }    };    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getMovie_title() {        return movie_title;    }    public void setMovie_title(String movie_title) {        this.movie_title = movie_title;    }    public String getMovie_overview() {        return movie_overview;    }    public void setMovie_overview(String movie_overview) {        this.movie_overview = movie_overview;    }    public String getMovie_backdrop() {        return movie_backdrop;    }    public void setMovie_backdrop(String movie_backdrop) {        this.movie_backdrop = movie_backdrop;    }    public String getMovie_poster() {        return movie_poster;    }    public void setMovie_poster(String movie_poster) {        this.movie_poster = movie_poster;    }    public String getMovie_release() {        return movie_release;    }    public void setMovie_release(String movie_release) {        this.movie_release = movie_release;    }    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeInt(id);        dest.writeString(movie_title);        dest.writeString(movie_overview);        dest.writeString(movie_backdrop);        dest.writeString(movie_poster);        dest.writeString(movie_release);    }}