package com.example.root.moviecatalogue.network;import java.io.IOException;import okhttp3.HttpUrl;import okhttp3.Interceptor;import okhttp3.Request;import okhttp3.Response;public class GetterAPIKey implements Interceptor{    private static String API_KEY = "233b568bea8fb27357da8dd3df2fc37f";    @Override    public Response intercept(Chain chain) throws IOException {        final HttpUrl url = chain.request()                .url()                .newBuilder()                .addQueryParameter("api_key", API_KEY)                .addQueryParameter("language", "en-US")                .build();        final Request request = chain.request().newBuilder().url(url).build();        return chain.proceed(request);    }}