package com.example.root.moviecatalogue.network;import android.database.Observable;import com.example.root.moviecatalogue.model.MovieResponse;import okhttp3.OkHttpClient;import okhttp3.logging.HttpLoggingInterceptor;import retrofit2.Retrofit;import retrofit2.adapter.rxjava2.RxJava2CallAdapterFactory;import retrofit2.converter.gson.GsonConverterFactory;import retrofit2.http.GET;import retrofit2.http.Query;public interface MovieService {    @GET("search/movie")    Observable<MovieResponse> searchMovies(@Query("query") String movieName) ;    @GET("movie/now_playing")    Observable<MovieResponse> getNowPlayingMovies();    @GET("movie/upcoming")    Observable<MovieResponse> getUpcomingMovies();    String BASE_URL = "https://api.themoviedb.org/3/";    class ServiceGetMovie {        public static MovieService instance(){            final HttpLoggingInterceptor loggingInterceptor = new HttpLoggingInterceptor();            loggingInterceptor.setLevel(HttpLoggingInterceptor.Level.BODY);            final GetterAPIKey getterAPIKey = new GetterAPIKey();            final OkHttpClient okHttpClient = new OkHttpClient.Builder()                    .addInterceptor(loggingInterceptor)                    .addInterceptor(getterAPIKey)                    .build();            final Retrofit retrofit = new Retrofit.Builder()                    .addConverterFactory(GsonConverterFactory.create())                    .addCallAdapterFactory(RxJava2CallAdapterFactory.create())                    .client(okHttpClient)                    .baseUrl(BASE_URL)                    .build();            return retrofit.create(MovieService.class);        }    }}