package com.example.root.moviecatalogue.views.navigasi;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.example.root.moviecatalogue.R;import com.example.root.moviecatalogue.model.MovieModel;import butterknife.BindView;import butterknife.ButterKnife;public class MainHolder extends RecyclerView.ViewHolder{    @BindView(R.id.movie_title)    TextView title;    @BindView(R.id.release)    TextView release;    @BindView(R.id.overview)    TextView overview;    @BindView(R.id.poster)    ImageView poster;    String POSTER_PATH = "";    public MainHolder(View itemView) {        super(itemView);        ButterKnife.bind(this, itemView);    }    public void bind(MovieModel movieModel, Context context){        Glide.with(context)                .load(POSTER_PATH + movieModel.getMovie_poster())                .placeholder(R.mipmap.ic_launcher)                .into(poster);        title.setText(movieModel.getMovie_title());        release.setText("Release date"+movieModel.getMovie_release());        overview.setText(movieModel.getMovie_overview());    }}